import React, { useState, useEffect, useRef } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [threadId, setThreadId] = useState(uuidv4());
  const [userId] = useState(uuidv4()); // Static user ID for the session
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const startNewChat = () => {
    setMessages([]);
    setThreadId(uuidv4());
    setInputMessage('');
    setSelectedImage(null);
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
    }
  };

  const removeImage = () => {
    setSelectedImage(null);
    // Reset the file input
    const fileInput = document.getElementById('image-input');
    if (fileInput) {
      fileInput.value = '';
    }
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() && !selectedImage) {
      return;
    }

    const userMessage = {
      id: uuidv4(),
      type: 'user',
      text: inputMessage,
      image: selectedImage ? URL.createObjectURL(selectedImage) : null,
      timestamp: new Date().toLocaleTimeString()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const formData = new FormData();
      formData.append('message', inputMessage);
      formData.append('thread_id', threadId);
      formData.append('user_id', userId);

      if (selectedImage) {
        formData.append('query_image', selectedImage);
      }

      const response = await fetch('http://127.0.0.1:8888/api/v1/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      const aiMessage = {
        id: uuidv4(),
        type: 'ai',
        text: data.response || 'Thank you for your message. I\'m here to help you find the perfect products!',
        timestamp: new Date().toLocaleTimeString()
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error sending message:', error);

      const errorMessage = {
        id: uuidv4(),
        type: 'ai',
        text: 'Sorry, I encountered an error. Please try again later.',
        timestamp: new Date().toLocaleTimeString(),
        isError: true
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      setInputMessage('');
      setSelectedImage(null);
      // Reset the file input
      const fileInput = document.getElementById('image-input');
      if (fileInput) {
        fileInput.value = '';
      }
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="app">
      <div className="chatbot-container">
        <div className="chatbot-header">
          <h1>üõçÔ∏è Product Search Chatbot</h1>
          <button className="new-chat-btn" onClick={startNewChat}>
            Start New Chat
          </button>
        </div>

        <div className="chat-messages">
          {messages.length === 0 && (
            <div className="welcome-message">
              <h2>Welcome to Product Search! üéâ</h2>
              <p>Ask me anything about products, upload images, or describe what you're looking for.</p>
              <p>I'm here to help you discover amazing products!</p>
            </div>
          )}

          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type} ${message.isError ? 'error' : ''}`}>
              <div className="message-content">
                <div className="message-header">
                  <span className="message-sender">
                    {message.type === 'user' ? 'You' : 'AI Assistant'}
                  </span>
                  <span className="message-time">{message.timestamp}</span>
                </div>

                {message.image && (
                  <div className="message-image">
                    <img src={message.image} alt="User uploaded" />
                  </div>
                )}

                <div className="message-text">
                  {message.text}
                </div>
              </div>
            </div>
          ))}

          {isLoading && (
            <div className="message ai">
              <div className="message-content">
                <div className="message-header">
                  <span className="message-sender">AI Assistant</span>
                  <span className="message-time">{new Date().toLocaleTimeString()}</span>
                </div>
                <div className="message-text">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        <div className="chat-input-container">
          <div className="input-wrapper">
            <textarea
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your message here... (Press Enter to send)"
              className="message-input"
              rows="1"
            />

            <div className="input-actions">
              <label htmlFor="image-input" className="image-upload-btn">
                üì∑
                <input
                  id="image-input"
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  style={{ display: 'none' }}
                />
              </label>

              <button
                onClick={sendMessage}
                disabled={isLoading || (!inputMessage.trim() && !selectedImage)}
                className="send-btn"
              >
                {isLoading ? '‚è≥' : '‚û§'}
              </button>
            </div>
          </div>

          {selectedImage && (
            <div className="selected-image">
              <img src={URL.createObjectURL(selectedImage)} alt="Selected" />
              <button onClick={removeImage} className="remove-image-btn">
                ‚úï
              </button>
              <span className="image-name">{selectedImage.name}</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
